INSERT INTO torgi_gov_ru.lotcards VALUES (13, '22000043270000000051_1', '22000043270000000051', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Право пользования недрами с целью геологического изучения, разведки и добычи полезных ископаемых (углеводородное сырье) на участке недр "Хатояхский" в Ямало-Ненецком автономном округе', 16642553, NULL, '2023-09-01 11:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 507.7, 'Сведения о запасах и ресурсах полезных ископаемых приведены в аукционной документации.', NULL, '643', 'ETP_GPB', '2023-08-01 09:55:49', 'МСК+2', '300', false, false, NULL, '"Хатояхский"', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Ямало-Ненецкий автономный округ', 'В соответствии с утвержденной аукционной документацией.', '7500.0', NULL, 'Уплачивается победителем Аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 136126.0, 'Перед подачей заявки лицо, желающее принять участие в Аукционе, перечисляет сбор за участие в Аукционе в размере и по реквизитам, которые указаны в приложении № 1 к Порядку и условиям проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска Заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте Российской Федерации в информационно-телекоммуникационной сети «Интернет» для размещения информации о проведении торгов протокола о результатах Аукциона, протокола рассмотрения заявок на участие в Аукционе, победитель Аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в с