INSERT INTO torgi_gov_ru.lotcards VALUES (98, '22000043270000000020_1', '22000043270000000020', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Право пользования участком недр  Янгъяхинский , расположенным на территории Ямало-Ненецкого автономного округа, для геологического изучения, разведки и добычи полезных ископаемых.', 23787913, 23787913, '2022-09-02 11:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 101.5, 'Сведения о ресурсах полезных ископаемых приведены в Порядке и условиях проведения аукциона.', NULL, '643', 'ETP_GPB', '2022-08-03 15:48:57', 'МСК+2', '300', false, false, NULL, 'Янгъяхинский', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Ямало-Ненецкий автономный округ', 'В соответствии с утвержденным Порядком и условиями проведения аукциона. ', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном  сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе. ', 131859.0, 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет  сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе. ', 'В течение 30 дней со дня размещения на официальном сайте  для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недра