INSERT INTO torgi_gov_ru.lotcards VALUES (173, '22000049380000000070_1', '22000049380000000070', '1', 'CANCELED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Западно-Ленский, расположенный на территории Ленского района Республики Саха (Якутия)', 10313617, NULL, '2024-10-07 09:00:00', NULL, NULL, 3232.7, 'Сведения о запасах и ресурсах полезных ископаемых указаны в Порядке и условиях проведения аукциона', NULL, '643', 'ETP_GPB', '2024-09-05 06:50:17', 'МСК+6', '540', false, false, NULL, 'Западно-Ленский', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Республика Саха (Якутия)', 'Порядок подачи заявок установлен Правилами и условиями настоящего аукциона', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 149489.0, 'В соответствии с положением п. 34 Правил, перед подачей заявки лицо, желающее принять участие в аукционе перечисляет сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах».', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет задаток за уча