INSERT INTO torgi_gov_ru.lotcards VALUES (14, '22000043270000000050_1', '22000043270000000050', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Право пользования недрами с целью геологического изучения, разведки и добычи полезных ископаемых (углеводородное сырье) на участке недр "Ванхетский" в Ямало-Ненецком автономном округе', 19551626, NULL, '2023-09-01 11:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 294.7, 'Сведения о запасах и ресурсах полезных ископаемых приведены в аукционной документации', NULL, '643', 'ETP_GPB', '2023-08-01 05:42:40', 'МСК+2', '300', false, false, NULL, '"Ванхетский"', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Ямало-Ненецкий автономный округ', 'В соответствии с утвержденной аукционной документацией.', '7500.0', NULL, 'Уплачивается победителем Аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 136126.0, 'Перед подачей заявки лицо, желающее принять участие в Аукционе, перечисляет сбор за участие в Аукционе в размере и по реквизитам, которые указаны в приложении № 1 к Порядку и условиям проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска Заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте Российской Федерации в информационно-телекоммуникационной сети «Интернет» для размещения информации о проведении торгов протокола о результатах Аукциона, протокола рассмотрения заявок на участие в Аукционе, победитель Аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах».', 'Перед подачей заявки лицо, желающее принять участие в Аукционе, перечисляет задаток за участие в Аукционе в размере и по реквизитам, которые указаны в приложении № 1 к Порядку и условиям проведения аукциона. Уплата задатка за участие в Аукционе является одним из условий допуска Заявителя к участию в Аукционе.', 'В соответствии с п.33 правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28 декабря 2021 г. № 2499.', '{"id": "22000043270000000050_1", "lotName": "Участок недр \"Ванхетский\"", "lotStatus": "FAILED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000043270000000050", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Право пользования недрами с целью геологического изучения, разведки и добычи полезных ископаемых (углеводородное сырье) на участке недр \"Ванхетский\" в Ямало-Ненецком автономном округе", "priceMin": 19551626, "priceStep": 1955162.6, "lotImages": [], "lotAttachments": [], "noticeAttachments": [{"fileId": "64c8a9ff36b6a92d6d337753", "signatureId": "64ca21f13a630c35d0097c10", "fileName": "Приказ №150 от 27_07_2023.pdf", "fileSize": 810057, "uploadDate": "2023-08-01T06:45:15.648+00:00", "hash": "1369e11504f2b724d9f4884c7ce878395284c6e547f8c04a5039153484fdac45", "attachmentTypeCode": "Auction_Document", "attachmentTypeName": "Документация аукциона", "inactive": false, "checkResult": "OK"}, {"fileId": "64c8aa0836b6a92d6d337759", "signatureId": "64ca21f34c972b694c6f7bd2", "fileName": "!Ванхетский приложения к ПРИК.docx", "fileSize": 928439, "uploadDate": "2023-08-01T06:45:26.032+00:00", "hash": "8664a116aded357f64226430e7ee27e658592afbd04bbbf24cf1