INSERT INTO torgi_gov_ru.lotcards VALUES (247, '22000049380000000089_1', '22000049380000000089', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Восточно-Молбинский, расположенном на территории МР «Олекминский район» Республики Саха (Якутия)', 18497173, 18497173, '2025-05-28 09:00:00', NULL, NULL, 5982.5, 'Сведения о запасах и ресурсах полезных ископаемых указаны в Порядке и условиях проведения аукциона', NULL, '643', 'ETP_GPB', '2025-04-02 05:34:53', 'МСК+6', '540', false, false, NULL, 'Восточно-Молбинский', 'Геологическое изучение, разведка и добыча полезных ископаемых', 'Республика Саха (Якутия)', 'Порядок подачи заявок установлен Правилами и условиями настоящего аукциона', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 159022.0, 'В соответствии с положением п. 34 Правил, перед подачей заявки лицо, желающее принять участие в аукционе перечисляет сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах».', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет задаток за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата задатка за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В соответствии с положением п. 33 Правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28.12.2021 № 2499.', '{"id": "22000049380000000089_1", "lotName": "Аукцион на право пользования участком недр Восточно-Молбинский, расположенном на территории МР «Олекминский район» Республики Саха (Якутия), с целью геологического изучения, разведки и добычи полезных ископаемых", "lotStatus": "FAILED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000049380000000089", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Восточно-Молбинский, расположенном на территории МР «Олекминский район» Республики Саха (Якутия)", "priceMin": 18497173, "priceStep": 1849717.3, "lotImages": [], "lotVideos": [], "noticeAttachments": [{"fileId": "67ecf1327ffbff2c41b3b496", "signatureId": "67ecf214c5662f317a5c6121", "fileName": "Приказ Якутнедр от 02.04.2025 №ЯКУ_35 (торги).pdf", "fileSize": 528006, "uploadDate": "2025-04-02T08:11:30.400+00:00", "hash": "650a5e039d15abb569c0445ea04321de927f7700b511d00f81f9fcc09147a30e", "attachmentTypeCode": "Auction_Document", "attachmentTypeName": "Документация аукциона", "inactive": false, "checkResult": "OK"}, {"fileId": "67ecf13bf82f6621372e98cf", "signatureId": "67ecf21482fad246dc5b131e", "fileName": "Приложение к приказу Восточно-Молбинский.docx", "fileSize": 80654, "uploadDate": "2025-04-02T08:11:40.311+00:00", "hash": "36ded014b6f7c3e82cd98edb55a99df36348c72a1d5867bfce096c291963d52d", "attachmentTypeCode":