INSERT INTO torgi_gov_ru.lotcards VALUES (162, '22000039810000000079_1', '22000039810000000079', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении Северный участок недр располагается в пределах Советского района Саратовской области. Площадь участка недр составляет 84,7 кв. км. В пределах Северного участка недр расположены Любимовское  (часть НФ), а также Северо-Васнецовское нефтегазоконденсатные месторождения.   В границах Северного участка особо охраняемые природные территории федерального, регионального, местного значения отсутствуют. В пределах Северного участка учтенные Государственным балансом запасов полезных ископаемых РФ по состоянию на 01.01.2023 извлекаемые запасы нефти, газа и конденсата составляют:нефти по категории С1 – 0,001 млн.т.; газа по категории С1 – 0,62 млрд.м3.; газа по категории С2 – 0,327 млрд.м3.; конденсата по категории С1 – 0,006 млн.т.По данным ФГБУ «ВНИГНИ» на основании количественной оценки УВС России (по состоянию на 01.01.2017) в пределах Северного участка извлекаемые ресурсы нефти и газа составляют:нефти по категории Dл – 0,4 млн. т; газа по категории Dл– 0,5 млрд.м3.', 29460321, NULL, '2024-08-05 20:59:00', NULL, NULL, 84.7, 'нефть (млн.т.)С1 – 0,001, Dл – 0,4;газ (млрд.м3)С1 – 0,62;С2 – 0,327,Dл– 0,5;кон.(млн.т)С1 – 0,006', NULL, '643', 'ETP_GPB', '2024-06-28 09:34:30', 'МСК', '180', false, false, NULL, 'Северный участок недр', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Саратовская область', 'В соответствии с Порядком и условиями проведения аукциона (приложение № 3 к приказу Приволжскнедра от 25.06.2024 № 170)', '7500.0', NULL, 'В течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 146855.0, 'В соответствии с Порядком и условиями проведения аукциона (приложение № 3 к приказу Приволжскнедра от 25.06.2024 № 170)', 'Окончательный размер разового п