INSERT INTO torgi_gov_ru.lotcards VALUES (252, '22000039810000000108_1', '22000039810000000108', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении Юрьевский участок недр располагается в пределах Гагаринского района Саратовской области. Площадь участка недр составляет 5,4 кв. км. В пределах Юрьевского участка недр расположена часть Александровского газонефтяного месторождения (отложения С1tl тульского горизонта нижнего карбона).  В границах Юрьевского участка особо охраняемые природные территории федерального, регионального, местного значения отсутствуют. В пределах Юрьевского участка учтенные Государственным балансом запасов полезных ископаемых РФ по состоянию на 01.01.2024 извлекаемые запасы нефти и газа составляют: нефти по категории В2 – 0,043млнт.; газа по категории В2 – 0,040 млрдм3. По результатам апробации результатов оценки прогнозных ресурсов углеводородного сырья (протокол №ОК-03-85/60-апр от 07.03.2025) в границах Юрьевского участка апробированы и приняты к учету прогнозные ресурсы: нефти: по категории D1 – 0,03 млнт.; газа: по категории D1– 0,07 млрдм3.', 6241861.0, 6241861.0, '2025-05-12 20:59:00', NULL, NULL, 5.4, 'нефть (млн.т.) В2 – 0,043, D1 – 0,03;  газ (млрд.м3) В2 – 0,040; D1– 0,07', NULL, '643', 'ETP_GPB', '2025-04-08 10:40:23', 'МСК', '180', false, false, NULL, 'Юрьевский участок недр', 'Геологическое изучение, разведка и добыча полезных ископаемых', 'Саратовская область', 'В соответствии с Порядком и условиями проведения аукциона (приложение № 3 к приказу Приволжскнедра от 08.04.2025 № 96)', '7500.0', NULL, 'В течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 159022.0, 'В соответствии с Порядком и условиями проведения аукциона (приложение № 3 к приказу Приволжскнедра от 08.04.2025 № 96)', 'Окончательный размер разового платежа за пользование недрами устанавливается в протоколе