INSERT INTO torgi_gov_ru.lotcards VALUES (42, '22000049380000000038_1', '22000049380000000038', '1', 'SUCCEED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Участок недр Горный, расположенном на территории Вилюйского, Горного, Кобяйского улусов (районов)', 26568815, 1548961914.5, '2023-07-19 09:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 12426.3, 'Сведения о запасах и ресурсах полезных ископаемых указаны в Порядке и условиях проведения аукциона', NULL, '643', 'ETP_GPB', '2023-04-19 08:44:54', 'МСК+6', '540', false, false, NULL, 'Горный', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Республика Саха (Якутия)', 'Порядок подачи заявок установлен Правилами и условиями настоящего аукциона', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 134688.0, 'В соответствии с положением п. 34 Правил, перед подачей заявки лицо, желающее принять участие в аукционе перечисляет сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Ро