INSERT INTO torgi_gov_ru.lotcards VALUES (53, '22000033960000000013_1', '22000033960000000013', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении участок недр Нижнеодесский (месторождения: Нижнеодесское) расположен на территории МО МР «Сосногорск» Республики Коми. Участок недр расположен в 15 км от административного центра г. Сосногорск и в 22 км от пос. Нижний Одес. В 6 км к северу от участка недр проходит автомобильная дорога с асфальтовым покрытием Ухта – Сосногорск – Нижний Одес, с которой он связан грунтовой автодорогой круглогодичного использования. Участок недр расположен в 30 км от ж/д станции Ухта, которая расположена на железнодорожной магистрали Москва – Воркута. Линии электропередач, а также источники водоснабжения на территории участка недр не расположены. Местность на участке недр сильно заболочена и характерна обилием ручьев. Доставка грузов осуществляется автомобильным транспортом по развитой сети грунтовых дорог.', 96164773, NULL, '2023-03-10 12:45:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 32.5, ' В соответствии с разделом II Приложения № 3 к приказу Севзапнедра от 25.01.2023 № 18', NULL, '643', 'ETP_GPB', '2023-01-26 13:33:17', 'МСК', '180', false, false, NULL, 'Нижнеодесский (месторождения: Нижнеодесское)', 'разведка и добыча полезных ископаемых', 'Республика Коми', 'В соответствии с разделом III Приложения № 3 к приказу Севзапнедра от 25.01.2023 № 18', '7500.0', NULL, 'В течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе победитель обязан уплатить государственную пошлину.', 131445.0, 'В соответствии с п. 15 Приложения № 3 к приказу Севзапнедра от 25.01.2023 № 18', 'В соответствии с п. 11 Приложения № 3 к приказу Севзапнедра от 25.01.2023 № 18', 'В соответствии с п. 13 Приложения № 3 к приказу Се