INSERT INTO torgi_gov_ru.lotcards VALUES (176, '22000049380000000063_1', '22000049380000000063', '1', 'SUCCEED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Южно-Джербинский расположенный на территории Ленского района Республики Саха (Якутия)', 7526911.0, 206990052.5, '2024-10-07 09:00:00', NULL, NULL, 2663.42, 'Сведения о запасах и ресурсах полезных ископаемых указаны в Порядке и условиях проведения аукциона', NULL, '643', 'ETP_GPB', '2024-09-05 07:29:45', 'МСК+6', '540', false, false, NULL, 'Южно-Джербинский', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Республика Саха (Якутия)', 'Порядок подачи заявок установлен Правилами и условиями настоящего аукциона', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 149489.0, 'В соответствии с положением п. 34 Правил, перед подачей заявки лицо, желающее принять участие в аукционе перечисляет сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах».', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет задаток за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата задатка за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В соответствии с положением п. 33 Правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28.12.2021 № 2499.', '{"id": "22000049380000000063_1", "lotName": "Аукцион на право пользования участком недр Южно-Джербинский, расположенном на территории Ленского района Республики Саха (Якутия), с целью геологического изучения, разведки и добычи полезных ископаемых", "lotStatus": "SUCCEED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000049380000000063", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Южно-Джербинский расположенный на территории Ленского района Республики Саха (Якутия)", "priceMin": 7526911.0, "priceStep": 752691.1, "lotImages": [], "lotAttachments": [], "noticeAttachments": [{"fileId": "66d974dc87bfd129bf90879e", "signatureId": "66d975044d110f650b109c87", "fileName": "Приказ Якутнедра №129 от 05.09.2024 Южно-Джербинский.pdf", "fileSize": 1141681, "uploadDate": "2024-09-05T09:07:39.332+00:00", "hash": "a9089cc19602905d16084643ffd59ea61de61b94ba3024747965c7438af529cb", "attachmentTypeCode": "Auction_Document", "attachmentTypeName": "Документация аукциона", "inactive": false, "checkResult": "OK"}, {"fileId": "66d974e8bc053e42ee70b6c0", "signatureId": "66d9750344422c02a356b2c5", "fileName": "Приложения к приказу Южно-Джербинский.docx", "fileSize": 86154, "uploadDate": "2024-09-05T09:07:53.463+00:00", "hash": "830fbb2be855b007e50ffb5bb490344c5a97fbe1c87498a914f41363ced3ba5c", "attachmentTypeCode": "Auction_Document