INSERT INTO torgi_gov_ru.lotcards VALUES (101, '22000043270000000017_1', '22000043270000000017', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Право пользования участком недр Худосейский, расположенным на территории Ямало-Ненецкого автономного округа, для геологического изучения, разведки и добычи полезных ископаемых.', 183203520, NULL, '2022-09-02 11:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 623.0, 'Сведения о ресурсах полезных ископаемых приведены в Порядке и условиях проведения аукциона.', NULL, '643', 'ETP_GPB', '2022-08-03 15:11:50', 'МСК+2', '300', false, false, NULL, 'Худосейский', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Ямало-Ненецкий автономный округ', 'В соответствии с утвержденным Порядком и условиями проведения аукциона. ', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном  сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе. ', 131859.0, 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет  сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе. ', 'В течение 30 дней со дня размещения на официальном сайте  для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах». ', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет  задаток за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата задатка за участие в аукционе является одним из условий допуска заявителя к участию в аукционе. ', 'В соответствии с положением п. 33 Правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28.12.2021 № 2499. ', '{"id": "22000043270000000017_1", "lotName": "Право пользования участком недр Худосейский", "lotStatus": "FAILED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000043270000000017", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Право пользования участком недр Худосейский, расположенным на территории Ямало-Ненецкого автономного округа, для геологического изучения, разведки и добычи полезных ископаемых.", "priceMin": 183203520, "priceStep": 18320352, "lotImages": [], "lotAttachments": [{"fileId": "62ea8fade58d230a43f7c34f", "signatureId": "62ea90a377c5540da57c7ba3", "fileName": "порядок и условия Худосейский.docx", "fileSize": 92616, "hash": "cc6551f19f0fae5bc5819bbd8a212e5b11572b0e6b5c0deae4f12cbc3fd9ffb9", "attachmentTypeCode": "Lot_Document", "attachmentTypeName": "Документация лота", "inactive": false}, {"fileId": "62ea8fd6e58d230a43f7c354", "signatureId": "62ea90a877c5540da57c7ba7", "fileName": "п-199.pdf", "fileSize": 637330, "hash": "45997b5e94f9bbb581a624ef5d8040064b6ad577c4d7cd2c5a19cdbfc96ea6db", "attachmentTypeCode": "Lot_Document", "attachmentTypeName": "Документация лота", "inactive": false}], "noticeAttachments": [], "protocols": [{"type": {"cod