INSERT INTO torgi_gov_ru.lotcards VALUES (54, '22000059140000000009_1', '22000059140000000009', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении Каспийский участок расположен на территории Лаганского района Республики Калмыкия. Предоставляется в пользование для разведки и добычи полезных ископаемых, в том числе использования отходов добычи полезных ископаемых и связанных с ней перерабатывающих производств, размещения в пластах горных пород попутных вод и вод, использованных пользователями недр для собственных производственных и технологических нужд при разведке и добыче углеводородного сырья, сроком на 20 лет с даты государственной регистрации лицензии.', 264362000, 264362000, '2023-01-27 20:59:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 133.7, 'Содержатся в Приложении №3  к приказу Югнедра от 27.12.2022 № 92 ', NULL, '643', 'ETP_GPB', '2022-12-27 11:45:46', 'МСК', '180', true, false, NULL, 'Каспийский участок', 'разведка и добыча полезных ископаемых', 'Республика Калмыкия', 'Основные условия пользования недрами изложены в разделе III. Приложения № 3 к приказу Югнедра от 27.12.2022 № 92 ', '7500.0', NULL, 'Гос. пошлина в размере 7500 рублей, предусмотренная абз. 2 пп. 92 п. 1 ст. 333.33 Налогового кодекса РФ, в соответствии с положениями Порядка № 735/04 уплачивается победителем Аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона РФ «О недрах», в течение 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, либо протокола рассмотрения заявок на участие в аукционе. ', 131445.0, 'п. 15 раздела I. Приложения № 3 к приказу Югнедра от 27.12.2022 № 92 ', 'п. 11 раздела I. Приложения № 3 к приказу Югнедра от 27.12.2022 № 92 ', 'Перед подачей заявки лицо, желающее принять участие