INSERT INTO torgi_gov_ru.lotcards VALUES (59, '22000039810000000026_1', '22000039810000000026', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении Каменный участок недр располагается в Красноармейском и Ровенском районах Саратовской области. Площадь участка недр составляет 244,6 кв. км.В пределах Каменного участка ресурсы нефти категории D0, учитываемые Государственным балансом запасов полезных ископаемых РФ по состоянию на 01.01.2022, составляют 8,612 млн.т извлекаемых (Каменная структура).По данным ФГБУ «ВНИГНИ» на основании количественной оценки УВС России (по состоянию на 01.01.2017) ресурсы нефти и газа в извлекаемой части составляют:-нефти по категории Dл – 1,388 млн. т;-газа по категории Dл – 1,0 млрд. м3.В границах Каменного участка недр особо охраняемые природные территории регионального и местного значения отсутствуют. В пределах Каменного участка недр расположены участки недр местного значения: Дубовское (участок 2) и Каменское месторождения. Из площади Каменного участка вышеуказанные участки недр местного значения исключены.', 124832948, 124832948, '2023-02-03 14:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 244.6, 'нефть D0-8,612млн.т ,нефть Dл-1,388млн.т, газ  Dл - 1,0млрд.м3', NULL, '643', 'ETP_GPB', '2022-12-23 11:50:43', 'МСК', '180', false, false, NULL, 'Каменный участок недр', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Саратовская область', NULL, '7500.0', NULL, 'В течении 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 131445.0, 'до 03.02.2023 в размере и по реквизитам, указанным в Порядке и условиях проведения аукциона', 'В течении 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 'До 03.02.20