INSERT INTO torgi_gov_ru.lotcards VALUES (60, '22000039810000000025_1', '22000039810000000025', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'В административном отношении Лазаревский участок недр располагается большей частью в пределах Красноармейского района и частично в пределах Энгельсского и Ровенского районов Саратовской области.Площадь участка недр составляет 228,5 кв. км.В пределах Лазаревского участка ресурсы нефти категории D0, учитываемые Государственным балансом запасов полезных ископаемых РФ по состоянию на 01.01.2022, составляют 7,78млн.т извлекаемых (Лазаревская структура). По данным ФГБУ «ВНИГНИ» на основании количественной оценки УВС России (по состоянию на 01.01.2017) ресурсы нефти и газа в извлекаемой части составляют:-нефти по категории Dл – 1,5 млн. т;-газа по категории D1– 1,0 млрд. м3.В границах Лазаревского участка недр особо охраняемые природные территории регионального и местного значения, а также участки недр местного значения отсутствуют.', 112227917, NULL, '2023-02-03 14:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 228.5, 'нефть D0-7,78 млн.т ,нефть Dл-1,5млн.т, газ D1-1,0млрд.м3', NULL, '643', 'ETP_GPB', '2022-12-23 11:36:06', 'МСК', '180', false, false, NULL, 'Лазаревский участок недр', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Саратовская область', NULL, '7500.0', NULL, 'В течении 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 131445.0, 'До 03.02.2023 в размере и по реквизитам, указанным в Порядке и условиях проведения аукциона', 'В течении 30 дней со дня размещения на официальном сайте протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе', 'До 03.02.2023 в размере и по реквизитам, указанным в Порядке и условиях проведения аукциона', 'В течении 5 рабочих 