INSERT INTO torgi_gov_ru.lotcards VALUES (49, '22000059140000000014_1', '22000059140000000014', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Наименование участка недр, являющегося объектом аукциона: Лободинское месторождение (часть).Местоположение участка недр: Быковский район Волгоградской области.Вид пользования недрами: разведка и добыча полезных ископаемых.Категория участка недр: участок недр, не относящийся к участкам недр федерального значения и участкам недр местного значения.Целевое назначение: разведка и добыча полезных ископаемых, в том числе использование отходов добычи полезных ископаемых и связанных с ней перерабатывающих производств, размещение в пластах горных пород попутных вод и вод, использованных пользователями недр для собственных производственных и технологических нужд при разведке и добыче углеводородного сырья.Виды полезных ископаемых на участке недр: углеводородное сырье.Статус участка недр: горный отвод.', 158142532, NULL, '2023-04-11 20:59:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 38.48, 'Содержатся в Приложении №3 к приказу Югнедра от 14.03.2023 №16', NULL, '643', 'ETP_GPB', '2023-03-14 15:02:33', 'МСК', '180', false, false, NULL, 'Лободинское месторождение (часть)', 'разведка и добыча полезных ископаемых', 'Волгоградская область', 'Основные условия пользования недрами изложены в разделе III. Приложения №3 к приказу Югнедра от 14.03.2023 №16', '7500.0', NULL, 'Гос. пошлина в размере 7500 руб., предусмотренная абз. 2 пп. 92 п. 1 ст. 333.33 Налогового кодекса РФ, в соответствии с положениями Порядка № 735/04 уплачивается победителем Аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона РФ«О недрах», в течение 30 дней со дня размещения на официальном сайте протокола о результатах Аукциона, либо прот