INSERT INTO torgi_gov_ru.lotcards VALUES (103, '22000043270000000015_1', '22000043270000000015', '1', 'SUCCEED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Право пользования участком недр Северо-Варейский 3 , расположенным на территории Ямало-Ненецкого автономного округа, для геологического изучения, разведки и добычи полезных ископаемых.', 17546748, 22810772.4, '2022-09-02 11:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 614.5, 'Сведения о ресурсах полезных ископаемых приведены в Порядке и условиях проведения аукциона.', NULL, '643', 'ETP_GPB', '2022-08-03 14:41:03', 'МСК+2', '300', false, false, NULL, 'Северо-Варейский 3 ', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Ямало-Ненецкий автономный округ', 'В соответствии с утвержденным Порядком и условиями проведения аукциона. ', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном  сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе. ', 131859.0, 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет  сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте  для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах». ', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет  задаток за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата задатка за участие в аукционе является одним из условий допуска заявителя к участию в аукционе. ', 'В соответствии с положением п. 33 Правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28.12.2021 № 2499. ', '{"id": "22000043270000000015_1", "lotName": "Право пользования участком недр Северо-Варейский 3", "lotStatus": "SUCCEED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000043270000000015", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Право пользования участком недр Северо-Варейский 3 , расположенным на территории Ямало-Ненецкого автономного округа, для геологического изучения, разведки и добычи полезных ископаемых.", "priceMin": 17546748, "priceStep": 1754674.8, "lotImages": [], "lotAttachments": [{"fileId": "62ea881de58d230a43f7c2ba", "signatureId": "62ea899baab05c28ef4f315c", "fileName": "порядок и условия Северо-Варейский 3.docx", "fileSize": 100026, "hash": "c9f1cdf1b3788c5369571a1e2481a6458198945e98b45ffba60d8d779dd64da8", "attachmentTypeCode": "Lot_Document", "attachmentTypeName": "Документация лота", "inactive": false}, {"fileId": "62ea883e90c36e63fec630e5", "signatureId": "62ea89a177c5540da57c7b26", "fileName": "п-197.pdf", "fileSize": 633589, "hash": "5a8403552e91f3f8a0b36370a8854a04701b88c0b5f1ca64cc7d7bf31fe1571d", "attachmentTypeCode": "Lot_Document", "attachmentTypeName": "Документация лота", "inactive": false}], "noticeA