INSERT INTO torgi_gov_ru.lotcards VALUES (107, '22000039810000000012_1', '22000039810000000012', '1', 'FAILED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Наименование участка недр, являющегося объектом аукциона: НовосулеймановскийМестоположение участка недр: Камешкирский, Кузнецкий, Неверкинский районы Пензенской областиВид пользования недрами: геологическое изучение, разведка и добыча полезных ископаемых.Категория участка недр: участок недр, не относящийся к участкам недр федерального значения и участкам недр местного значения.Целевое назначение: для геологического изучения, разведки и добычи полезных ископаемых, в том числе использования отходов добычи полезных ископаемых и связанных с ней перерабатывающих производств.Виды полезных ископаемых на участке недр: углеводородное сырье.Статус участка недр: геологический отвод и горный отводСведения о запасах и ресурсах полезных ископаемых в пределах Новосулеймановского участка ресурсы углеводородного сырья составляют:  - нефть: по категории DЛ – 1,2 млн.т., D1 - 4,6 млн.т., D2 – 0,5 млн.т.- газ: по категории D1 – 1,2 млр. м3., D2 – 0,2 млр. м3.', 13255000, 13255000, '2022-08-31 21:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 1108.6, '- нефть: по категории DЛ – 1,2 млн.т., D1 - 4,6 млн.т., D2 – 0,5 млн.т.; газ: по категории D1 – 1,2 ', NULL, '643', 'ETP_GPB', '2022-07-26 08:42:01', 'МСК', '180', false, false, NULL, 'Новосулеймановский', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Пензенская область', 'Срок пользования участком недр составляет 25 лет и исчисляется с даты государственной регистрации лицензии на пользование недрами, которая определяется в автоматическом режиме как день внесения записи о государственной регистрации лицензии в государственный реестр участков недр, предоставленных в пользование, и лицензий на пользование недрами, предусмотренный ст. 28 Закона Р