INSERT INTO torgi_gov_ru.lotcards VALUES (41, '22000049380000000039_1', '22000049380000000039', '1', 'SUCCEED', '2499PP', 'Продажа права пользования участком недр', 'EA', 'Электронный аукцион', 'Участок недр Сунтарский 1, расположенном на территории Вилюйского, Верхневилюйского, Нюрбинского улусов (районов)', 177965508, 836437887.6, '2023-07-19 09:00:00', 'Углеводородное сырье', 'Участок недр, за исключением участков недр федерального значения и участков недр местного значения', 24774.0, 'Сведения о запасах и ресурсах полезных ископаемых указаны в Порядке и условиях проведения аукциона', NULL, '643', 'ETP_GPB', '2023-04-19 08:52:21', 'МСК+6', '540', false, false, NULL, 'Участок недр Сунтарский 1', 'геологическое изучение, разведка и добыча полезных ископаемых', 'Республика Саха (Якутия)', 'Порядок подачи заявок установлен Правилами и условиями настоящего аукциона', '7500.0', NULL, 'Государственная пошлина уплачивается победителем аукциона или иным лицом, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах», в течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе.', 134688.0, 'В соответствии с положением п. 34 Правил, перед подачей заявки лицо, желающее принять участие в аукционе перечисляет сбор за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата сбора за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В течение 30 дней со дня размещения на официальном сайте для размещения информации о проведении торгов протокола о результатах аукциона, протокола рассмотрения заявок на участие в аукционе, победитель аукциона или иное лицо, которому предоставляется право пользования недрами и оформляется лицензия на пользование недрами в соответствии с ч. 8 ст. 13.1 Закона Российской Федерации «О недрах».', 'Перед подачей заявки лицо, желающее принять участие в аукционе, перечисляет задаток за участие в аукционе в размере и по реквизитам, которые указаны в порядке и условиях проведения аукциона. Уплата задатка за участие в аукционе является одним из условий допуска заявителя к участию в аукционе.', 'В соответствии с положением п. 33 Правил проведения аукциона на право пользования участком недр федерального значения, участком недр местного значения, а также участком недр, не отнесенным к участкам недр федерального или местного значения, в электронной форме, утвержденных постановлением Правительства Российской Федерации от 28.12.2021 № 2499.', '{"id": "22000049380000000039_1", "lotName": "Аукцион на право пользования участком недр Сунтарский 1, расположенном на территории Вилюйского, Верхневилюйского, Нюрбинского улусов (районов) Республики Саха (Якутия), для геологического изучения, разведки и добычи полезных ископаемых, в том числе использования отходов добычи полезных ископаемых и связанных с ней перерабатывающих производств", "lotStatus": "SUCCEED", "biddForm": {"code": "EA", "name": "Электронный аукцион"}, "noticeNumber": "22000049380000000039", "lotNumber": 1, "biddType": {"code": "2499PP", "name": "Продажа права пользования участком недр"}, "lotDescription": "Участок недр Сунтарский 1, расположенном на территории Вилюйского, Верхневилюйского, Нюрбинского улусов (районов)", "priceMin": 177965508, "priceStep": 17796550.8, "lotImages": [], "lotAttachments": [], "noticeAttachments": [{"fileId": "643fac49ea016a6f460aad81", "signatureId": "643fad9e9f85b5509a38f0eb", "fileName": "Приказ 82 Сунтарский 1.pdf", "fileSize": 539202, "uploadDate": "2023-04-19T08:54:32.348+00:00", "hash": "5d2ad2e9103682143386292386917dd3175c30e27675237ad70776480fd33157", "attachmentTypeCode": "Auction_Document", "attachmentTypeName": "Документация аукциона", "inactive": false, "checkResult": "OK"}, {"fileId": "643fac48ea016a6f